var isLoggedIn = false;

function renderConditional() {
  if (isLoggedIn) {
    return <h1>Hello</h1>;
  }
  else {
    return <form className="form">
      <input type="text" placeholder="Username" />
      <input type="password" placeholder="Password" />
      <button type="submit">Login</button>
    </form>;
  }
}

function App() {
  return (
    renderConditional()
  );
}


export default App;
