import React, { useState } from 'react';


function App() {
  var count = 0;

  function increase() {
    count++;
  }

  return (
    <div>
      <div className="container">
        <h1>{count}</h1>
        <button onClick={increase}>+</button>
      </div>
    </div>

  );
}

export default App;
