import { useState } from "react";
import ToDoItem from "./ToDoItem";

function App() {

  const [inputText, setInputText] = useState("");
  const [items, setItems] = useState([]);

  function handleChange(e) {
    setInputText(e.target.value);
  }
  function addItem() {
    setItems((prevItems) => {
      return [...prevItems, inputText];
