    });
  }

  return (
    <div className="container">
      <div className="heading">
        <h1>To-Do List</h1>
      </div>
      <div className="form">
        <input onChange={handleChange} type="text" value={inputText} />
        <button onClick={addItem}>
          <span>Add</span>
        </button>
      </div>
      <div>
        <ul>
          {items.map((toDoItem, index) => (
          <ToDoItem 
          key={index}
          id={index}
          text={toDoItem}
          onChecked={deleteItem}
          />
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
